package day1;

public class Program3_datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   boolean result = true;
   //boolean takes only true or false--1 byte
   System.out.println(result);
   byte age=127;
   //byte has range from -128 to 127--1 byte
   System.out.println(age);
   short data=-32000;
   //short has range from -32000 to 32000--2 byte
   System.out.println(data);
   char alphabet='D';
   //char takes only one alphabet or one character inside single quotes it can be special charactor too
   System.out.println(alphabet);
   float marks1=9.87642331F;
   System.out.println(marks1);
   float marks2=987.642331F;
   System.out.println(marks2);
   //float needs F as suffix at the end of value assigned and float takes only 7 values totally--4 bytes
   double marks3=9.87642331;
   System.out.println(marks3);
   double marks4=987.6423310000000000000000;
   System.out.println(marks4);
   //double takes 15 values totally--8 bytes
   
   
	}

}
